export const initFileInputs=()=>{document.querySelectorAll(".input-file").forEach(e=>{let t=e.querySelector('input[type="file"]');const n=e.querySelector(".input-file__name");e.addEventListener("click",()=>{t.value&&(t.value="",n.textContent="Прикрепить файл")}),t.addEventListener("change",()=>{t.value?n.textContent=t.value.split("\\").pop():n.textContent="Прикрепить файл"})})};