export const initSelects=()=>{document.querySelectorAll(".dropdown").forEach(e=>{const o=e.querySelector(".dropdown__button"),t=e.querySelector(".dropdown__body"),n=t.querySelectorAll(".dropdown__list-item"),d=e.querySelector(".dropdown__input-hidden");o.addEventListener("click",function(e){t.classList.toggle("dropdown__body_visible"),this.classList.add("dropdown__button_active")}),n.forEach(function(e){e.addEventListener("click",function(e){e.stopPropagation(),o.innerText=this.innerText,o.focus(),d.value=this.dataset.value,t.classList.remove("dropdown__body_visible"),o.classList.remove("dropdown__button_active")})}),document.addEventListener("click",function(e){e.target!==o&&(o.classList.remove("dropdown__button_active"),t.classList.remove("dropdown__body_visible"))}),document.addEventListener("keydown",function(e){"Tab"!==e.key&&"Escape"!==e.key||(o.classList.remove("dropdown__button_active"),t.classList.remove("dropdown__body_visible"))})})};